import React, { Component } from 'react';
import "./osero.css";

var mas = new Array(9);

export default class Todo extends Component  {

    constructor (props) {
        super(props);
        this.state = {
            turn:true,
            result:0
        };
    }
    
    DoReset = () =>{
        document.getElementById("osero11").value = "";
        document.getElementById("osero12").value = "";
        document.getElementById("osero13").value = "";
        document.getElementById("osero21").value = "";
        document.getElementById("osero22").value = "";
        document.getElementById("osero23").value = "";
        document.getElementById("osero31").value = "";
        document.getElementById("osero32").value = "";
        document.getElementById("osero33").value = "";
    }

    Check_Shohai = (turn) => {
        var ret = 0;
        if(mas[0] == turn && mas[1] == turn && mas[2] == turn){
            ret =turn;
        }else if(mas[3] == turn && mas[4] == turn && mas[5] == turn){
            ret = turn;
        }else if(mas[6] == turn && mas[7] == turn && mas[8] == turn){
            ret = turn;
        }else if(mas[0] == turn && mas[3] == turn && mas[6] == turn){
            ret = turn;
        }else if(mas[1] == turn && mas[4] == turn && mas[7] == turn){
            ret = turn;
        }else if(mas[2] == turn && mas[5] == turn && mas[8] == turn){
            ret = turn;
        }else if(mas[0] == turn && mas[4] == turn && mas[8] == turn){
            ret = turn;
        }else if(mas[3] == turn && mas[4] == turn && mas[6] == turn){
            ret = turn;
        }

        return ret;
    }

    DoMark = (ids,x,y) => {
        const {turn} = this.state;
        const {result} = this.state;

        var flg = false;
        x = x -1;
        y = y -1;
        const TurnMessage = document.getElementById("TurnId");
        
        if(result !=0){
            this.DoReset();
            return
        }

        var win_flg = 0;
        if(turn == true){
            document.getElementById(ids).value = "〇";
            document.getElementById(ids).style.color = "red";
            
            mas[y*3 + x] = 1;
            win_flg = this.Check_Shohai(1);
            flg = false;
        }else{
            document.getElementById(ids).value = "×";
            document.getElementById(ids).style.color = "blue";
            TurnMessage.textContent = "プレイヤー1のターン";
            mas[y*3 + x] = 2;
            win_flg = this.Check_Shohai(2);
            flg =true;
        }
        this.setState({
            turn:flg
        });
        this.setState({
            result:win_flg
        })

        if(win_flg ==1){
            TurnMessage.textContent = "プレイヤー1の勝利！！";
            TurnMessage.style.color = "red";
        }else if(win_flg == 2){
            TurnMessage.textContent = "プレイヤー2の勝利！！";
            TurnMessage.style.color = "blue";
        }else if(turn == true){
            TurnMessage.textContent = "プレイヤー2のターン";
        }else{
            TurnMessage.textContent = "プレイヤー1のターン";
        }
    }

    onClick11 = () => {
        this.DoMark("osero11",1,1);
    }
    onClick12 = () => {
        this.DoMark("osero12",1,2);
    }
    onClick13 = () => {
        this.DoMark("osero13",1,3);
    }
    onClick21 = () => {
        this.DoMark("osero21",2,1);
    }
    onClick22 = () => {
        this.DoMark("osero22",2,2);
    }
    onClick23 = () => {
        this.DoMark("osero23",2,3);
    }
    onClick31 = () => {
        this.DoMark("osero31",3,1);
    }
    onClick32 = () => {
        this.DoMark("osero32",3,2);
    }
    onClick33 = () => {
        this.DoMark("osero33",3,3);
    }
    
    render() {
      return (<div>
        <h1 className="title">&nbsp;
            <u>〇×ゲーム</u>
        </h1>
        <table>
            <tbody>
                <tr>
                    <td><input type="text" id="osero11" onClick={this.onClick11} className="Mas" /></td>
                    <td><input type="text" id="osero12" onClick={this.onClick12} className="Mas" /></td>
                    <td><input type="text" id="osero13" onClick={this.onClick13} className="Mas" /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero21" onClick={this.onClick21} className="Mas" /></td>
                    <td><input type="text" id="osero22" onClick={this.onClick22} className="Mas" /></td>
                    <td><input type="text" id="osero23" onClick={this.onClick23} className="Mas" /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero31" onClick={this.onClick31} className="Mas" /></td>
                    <td><input type="text" id="osero32" onClick={this.onClick32} className="Mas" /></td>
                    <td><input type="text" id="osero33" onClick={this.onClick33} className="Mas" /></td>
                </tr>
            </tbody>
        </table>
        <h1 id="TurnId" className="Message">プレイヤー1のターン</h1>
        </div>);
    }
  }
