import React, { Component } from 'react';

export default class Todo extends Component  {

    constructor (props) {
        super(props);
        this.state = {
            turn:false,
            mas:[3,3],
            result:0
        };
    }
  
    DoMark = (ids,x,y) => {
        const {turn} = this.state;
        const {mas} = this.state;
        mas[Number(x),Number(y)] = this.state;

        var flg = false;
        if(turn == true){
            document.getElementById(ids).value = "〇";
            flg = false;
        }else{
            document.getElementById(ids).value = "×";
            flg =true;
        }
        this.setState({
            turn:flg
        });
    }

    onClick11 = () => {
        this.DoMark("osero11",1,1);
    }
    onClick12 = () => {
        this.DoMark("osero12",1,2);
    }
    onClick13 = () => {
        this.DoMark("osero13",1,3);
    }
    onClick21 = () => {
        this.DoMark("osero21",2,1);
    }
    onClick22 = () => {
        this.DoMark("osero22",2,2);
    }
    onClick23 = () => {
        this.DoMark("osero23",2,3);
    }
    onClick31 = () => {
        this.DoMark("osero31",3,1);
    }
    onClick32 = () => {
        this.DoMark("osero32",3,2);
    }
    onClick33 = () => {
        this.DoMark("osero33",3,3);
    }
    
    render() {
      return (
        <table border="3">
            <tbody>
                <tr>
                    <td><input type="text" id="osero11" onClick={this.onClick11} /></td>
                    <td><input type="text" id="osero12" onClick={this.onClick12} /></td>
                    <td><input type="text" id="osero13" onClick={this.onClick13} /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero21" onClick={this.onClick21} /></td>
                    <td><input type="text" id="osero22" onClick={this.onClick22} /></td>
                    <td><input type="text" id="osero23" onClick={this.onClick23} /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero31" onClick={this.onClick31} /></td>
                    <td><input type="text" id="osero32" onClick={this.onClick32} /></td>
                    <td><input type="text" id="osero33" onClick={this.onClick33} /></td>
                </tr>
            </tbody>
        </table>
      );
    }
  }
