import React, { Component } from 'react';

export default class Todo extends Component  {

    constructor (props) {
        super(props);
        this.state = {
            turn:false,
            result:0
        };
    }
  
    DoMark = () => {
        const {turn} = this.state;
        if(turn == true){
            document.getElementById("osero12").value = "〇";
            turn = false;
        }else{
            document.getElementById("osero12").value = "×";
            turn =true;
        }
    }

    onClick11() {
        this.DoMark();
    }
    onClick12() {
        this.DoMark("osero12");
    }
    onClick13() {
        this.DoMark("osero13");
    }
    onClick21() {
        document.getElementById("osero21").value = "〇";
    }
    onClick22() {
        document.getElementById("osero22").value = "〇";
    }
    onClick23() {
        document.getElementById("osero23").value = "〇";
    }
    onClick31() {
        document.getElementById("osero31").value = "〇";
    }
    onClick32() {
        document.getElementById("osero32").value = "〇";
    }
    onClick33() {
        document.getElementById("osero33").value = "〇";
    }
    
    render() {
      return (
        <table border="3">
            <tbody>
                <tr>
                    <td><input type="text" id="osero11" onClick={this.onClick11} /></td>
                    <td><input type="text" id="osero12" onClick={this.onClick12} /></td>
                    <td><input type="text" id="osero13" onClick={this.onClick13} /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero21" onClick={this.onClick21} /></td>
                    <td><input type="text" id="osero22" onClick={this.onClick22} /></td>
                    <td><input type="text" id="osero23" onClick={this.onClick23} /></td>
                </tr>
                <tr>
                    <td><input type="text" id="osero31" onClick={this.onClick31} /></td>
                    <td><input type="text" id="osero32" onClick={this.onClick32} /></td>
                    <td><input type="text" id="osero33" onClick={this.onClick33} /></td>
                </tr>
            </tbody>
        </table>
      );
    }
  }
